import React, { useState } from "react";
import {
  TextField,
  Typography,
  Button,
  Paper,
  FormControl,
  Box,
} from "@mui/material";
import FileBase from "react-file-base64";

import useStyles from "./styles";

const Form = () => {
  const classes = useStyles();
  const [postData, setPostData] = useState({
    creator: "",
    title: "",
    message: "",
    tags: "",
    seletedFile: "",
  });
  const handleSubmit = () => {};
  const clear = () => {};
  return (
    <Paper sx={{ padding: 2, margin: 2 }}>
      <FormControl
        sx={{ display: "flex", flexWrap: "wrap", justifyContent: "center" }}
        autoComplete="off"
        noValidate
        onSubmit={handleSubmit}
      >
        <Typography variant="h6">Creating a memrory</Typography>
        <TextField
          name="creator"
          variant="outlined"
          label="creator"
          fullWidth
          value={postData.creator}
          onChange={(e) => {
            setPostData({ ...postData, creator: e.target.value });
          }}
        />
        <TextField
          name="title"
          variant="outlined"
          label="title"
          fullWidth
          value={postData.title}
          onChange={(e) => {
            setPostData({ ...postData, title: e.target.value });
          }}
        />
        <TextField
          name="message"
          variant="outlined"
          label="message"
          fullWidth
          value={postData.message}
          onChange={(e) => {
            setPostData({ ...postData, message: e.target.value });
          }}
        />
        <TextField
          name="tags"
          variant="outlined"
          label="tags"
          fullWidth
          value={postData.tags}
          onChange={(e) => {
            setPostData({ ...postData, tags: e.target.value });
          }}
        />
        <Box sx={{ width: "97%", margin: "10px 0" }}>
          <FileBase
            type="file"
            multiple={false}
            onDone={({ base64 }) =>
              setPostData({ ...postData, seletedFile: base64 })
            }
          />
        </Box>
        <Button
          sx={{ marginBottom: 2 }}
          variant="contained"
          color="primary"
          size="large"
          type="submit"
          fullwidth
        >
          Submit
        </Button>
        <Button
          variant="contained"
          color="primary"
          size="small"
          onClick={clear}
          fullwidth
        >
          Clear
        </Button>
      </FormControl>
    </Paper>
  );
};

export default Form;
